def main():
    """Main function to load all examples into vector store"""
    print(f"=======================================================")
    print(f"  IYI RESTAURANT - COMPLETE EXAMPLES LOADER")
    print(f"=======================================================")
    print(f"\nTotal examples to load: {len(EXAMPLES)}")
    
    EXAMPLES_DB_DIR = "../vector_store_examples"
    
    print("\n[STEP 1] Deleting old vector store...")
    if os.path.exists(EXAMPLES_DB_DIR):
        shutil.rmtree(EXAMPLES_DB_DIR)
        print(f"  [OK] Deleted {EXAMPLES_DB_DIR}")
    else:
        print(f"  [INFO] No existing vector store found")
    
    print("\n[STEP 2] Creating new vector store directory...")
    os.makedirs(EXAMPLES_DB_DIR, exist_ok=True)
    print(f"  [OK] Created {EXAMPLES_DB_DIR}")
    
    print("\n[STEP 3] Initializing embeddings model...")
    embeddings = OllamaEmbeddings(model="nomic-embed-text")
    print(f"  [OK] Using nomic-embed-text model")
    
    print("\n[STEP 4] Creating Chroma vector store...")
    vector_store = Chroma(
        persist_directory=EXAMPLES_DB_DIR,
        embedding_function=embeddings
    )
    print(f"  [OK] Vector store initialized")
    
    print("\n[STEP 5] Preparing documents...")
    documents = []
    for idx, (prompt, response) in enumerate(EXAMPLES):
        full_example = f"User: {prompt}\nAgent: {response}"
        
        doc = Document(
            page_content=prompt,
            metadata={
                "full_example": full_example,
                "example_id": f"ex_{idx}"
            }
        )
        documents.append(doc)
    
    print(f"  [OK] Prepared {len(documents)} documents")
    
    print("\n[STEP 6] Adding examples to vector store...")
    print("  (This will take several minutes for embedding generation...)")
    
    # Add in batches for better progress tracking
    batch_size = 50
    total_batches = (len(documents) - 1) // batch_size + 1
    
    for i in range(0, len(documents), batch_size):
        batch = documents[i:i+batch_size]
        vector_store.add_documents(batch)
        batch_num = i // batch_size + 1
        print(f"  [OK] Batch {batch_num}/{total_batches} added ({len(batch)} examples)")
    
    final_count = vector_store._collection.count()
    
    print(f"\n=======================================================")
    print(f"  [SUCCESS] SUCCESS!")
    print(f"=======================================================")
    print(f"\nTotal examples in vector store: {final_count}")
    print(f"\n[INFO] How the system works:")
    print(f"  1. User asks a question")
    print(f"  2. System searches through ALL {final_count} examples")
    print(f"  3. Finds the 3 NEAREST matches using similarity")
    print(f"  4. Uses those 3 examples to format the response")
    print(f"\n[NOTE] The AI will now respond exactly like the examples!")
    print(f"\nNext step: Restart your server with:")
    print(f"  cd d:\\AI sales agent\\model_training\\scripts")
    print(f"  python server.py")
    print(f"\n=======================================================\n")

if __name__ == "__main__":
    main()

